var processbar = require('cli-progress-bar');

var _bar = new processbar();

function bar () {

};

bar.prototype.update = function (index, all, commit) {

	_bar.show(commit, (index/all).toFixed(2));
	if((all - index) < 10) {
		_bar.show(commit, 1);
	}
	_bar.pulse(index);
};

bar.prototype.complete = function () {

	_bar.show("complete", 1);
}

module.exports = bar;